var mongoose=require("mongoose"),bcrypt=require("bcrypt-nodejs"),userSchema=mongoose.Schema({local:{email:String,password:String},facebook:{id:String,token:String,email:String,name:String},twitter:{id:String,token:String,displayName:String,username:String},google:{id:String,token:String,email:String,name:String}});userSchema.methods.generateHash=function(e){return bcrypt.hashSync(e,bcrypt.genSaltSync(8),null)},userSchema.methods.validPassword=function(e){return bcrypt.compareSync(e,this.local.password)},module.exports=mongoose.model("User",userSchema);